00174     }
00175 
00176     // Then look for sub-parameters
00177     for (size_t i = 0; i < argxc->optionsCount; i++)
00178     {
00179         ArgxcOptions *opt = &argxc->options[i];
00180 
00181         // Check if the parent option exists in the arguments
00182         bool parentExists = false;
00183         for (size_t j = 0; j < argxc->mainArgsCount; j++)
00184         {
00185             if ((opt->param && strcmp(argxc->mainArgs[j], opt->param) == 0) ||
00186                     (opt->sparam && strcmp(argxc->mainArgs[j], opt->sparam) == 0))
00187             {
00188                 parentExists = true;
00189                 break;
00190             }
00191         }
00192 
00193         if (parentExists)
00194         {
00195             // Find the index of the requested sub-parameter
00196             for (size_t j = 0; j < opt->subParamsCount; j++)
00197             {
00198                 if (opt->subParams[j].id && strcmp(opt->subParams[j].id, id) == 0)
