00499 bool argxcCompareArgs(ArgxcOptions *options, size_t optionsCount, char **argv, size_t argvCount)
00500 {
00501     if (!options || !argv) return false;
00502 
00503     for (size_t i = 1; i < argvCount; i++)
00504     {
00505         const char *arg = argv[i];
00506 
00507         bool found = false;
00508         bool hasSubParams = false;
00509         bool hasAnySubParams = false;
00510         ArgxcOptions *matchedOption = NULL;
00511 
00512         // Find the matching option
00513         for (size_t j = 0; j < optionsCount; j++)
00514         {
00515             if ((options[j].sparam && strcmp(options[j].sparam, arg) == 0) ||
00516                     (options[j].param && strcmp(options[j].param, arg) == 0))
00517             {
00518                 found = true;
00519                 hasSubParams = options[j].hasSubParams;
00520                 hasAnySubParams = options[j].hasAnySubParams;
00521                 matchedOption = &options[j];
00522                 break;
00523             }
00524         }
00525 
00526         if (!found) return false;
00527 
00528         if (hasSubParams || hasAnySubParams)
00529         {
00530             // Check if there's a next argument
00531             if (i + 1 < argvCount)
00532             {
00533                 const char *nextArg = argv[i + 1];
00534 
00535                 // Check if next argument is a sub-parameter
00536                 bool isSubParam = false;
00537 
00538                 if (nextArg && strlen(nextArg) > 0 && nextArg[0] != '-')
00539                 {
00540                     // Validate if it's a valid sub-parameter
00541                     for (size_t k = 0; k < matchedOption->subParamsCount; k++)
00542                     {
00543                         if ((matchedOption->subParams[k].param && strcmp(matchedOption->subParams[k].param, nextArg) == 0) ||
00544                                 (matchedOption->subParams[k].sparam && strcmp(matchedOption->subParams[k].sparam, nextArg) == 0))
00545                         {
00546                             isSubParam = true;
00547                             break;
00548                         }
00549                     }
00550 
00551                     if (isSubParam)
00552                     {
00553                         i++; // Skip the sub-parameter
00554                     } else if (hasSubParams || hasAnySubParams)
00555                     {
00556                         return false;
00557                     }
00558                 } else if (hasSubParams || hasAnySubParams)
00559                 {
00560                     return false;
00561                 }
00562             }
00563         }
00564     }
00565 
00566     return true;
00567 }
