00096     int Argx::findParam(const std::string &id)
00097     {
00098         // First check if it's a main parameter
00099         for (size_t i = 0; i < this->options.size(); i++)
00100         {
00101             if (this->options[i].id == id)
00102             {
00103                 // Check if this main parameter exists in arguments
00104                 for (const std::string &arg : *this->mainArgs)
00105                 {
00106                     if (arg == this->options[i].param || arg == this->options[i].sparam)
00107                     {
00108                         return static_cast<int>(i);
00109                     }
00110                 }
00111             }
00112         }
00113 
00114         // Then look for sub-parameters
00115         for (const auto &opt : this->options)
00116         {
00117             // Check if the parent option exists in the arguments
00118             bool parentExists = false;
00119 
00120             for (const std::string &arg : *this->mainArgs)
00121             {
00122                 if (arg == opt.param || arg == opt.sparam)
00123                 {
00124                     parentExists = true;
00125                     break;
00126                 }
00127             }
00128 
00129             if (parentExists)
00130             {
00131                 // Find the index of the requested sub-parameter
00132                 for (size_t i = 0; i < opt.subParams.size(); i++)
00133                 {
00134                     if (opt.subParams[i].id == id) return static_cast<int>(i);
00135                 }
00136             }
00137         }
00138 
00139         return -1; // Not found
00140     }
