00178         for (const auto &opt : this->options)
00179         {
00180             if (opt.id == id)
00181             {
00182                 // Find the position of the main option in arguments
00183                 int mainOptionPos = -1;
00184 
00185                 for (size_t i = 0; i < this->mainArgs->size(); ++i)
00186                 {
00187                     if ((*this->mainArgs)[i] == opt.param || (*this->mainArgs)[i] == opt.sparam)
00188                     {
00189                         result.exists = true;
00190                         mainOptionPos = i;
00191                         break;
00192                     }
00193                 }
00194 
00195                 if (result.exists)
00196                 {
00197                     if (opt.hasSubParams || opt.hasAnySubParams)
00198                     {
00199                         // Check each sub-parameter
00200                         for (const auto &sub : opt.subParams)
00201                         {
00202                             bool subMatched = false;
00203 
00204                             // Look for sub-parameters after the main option
00205                             for (size_t i = mainOptionPos + 1; i < this->mainArgs->size(); ++i)
00206                             {
00207                                 if ((*this->mainArgs)[i] == sub.param || (*this->mainArgs)[i] == sub.sparam)
00208                                 {
00209                                     subMatched = true;
00210                                     break;
00211                                 }
00212                             }
00213 
00214                             result.subExists.push_back(subMatched);
00215                         }
00216                     }
00217 
00218                     return result;
00219                 }
00220             }
00221         }
00222 
00223         // If not found as top-level, check if it's a sub-parameter
00224         for (const auto &opt : this->options)
00225         {
00226             // Find if the parent option exists and get its position
00227             size_t parentPos = -1;
00228 
00229             for (size_t i = 0; i < this->mainArgs->size(); ++i)
00230             {
00231                 if ((*this->mainArgs)[i] == opt.param || (*this->mainArgs)[i] == opt.sparam)
00232                 {
00233                     parentPos = i;
00234                     break;
00235                 }
00236             }
00237 
00238             if (parentPos > -1 && (opt.hasSubParams || opt.hasAnySubParams))
00239             {
00240                 // Check if the requested sub-parameter exists after the parent
00241                 for (const auto &sub : opt.subParams)
00242                 {
00243                     if (sub.id == id)
00244                     {
00245                         for (size_t i = parentPos + 1 ; i < this->mainArgs->size(); ++i)
00246                         {
00247                             if ((*this->mainArgs)[i] == sub.param || (*this->mainArgs)[i] == sub.sparam)
00248                             {
00249                                 result.exists = true;
00250                                 break;
00251                             }
00252                         }
00253 
00254                         if (!result.exists && parentPos + 1 < this->mainArgs->size())
00255                         {
00256                             std::string nextArg = (*this->mainArgs)[parentPos + 1];
00257 
00258                             if (nextArg == sub.param || nextArg == sub.sparam) result.exists = true;
00259                         }
00260 
