00293     std::string Argx::createDocs(ARGXStyle style, const std::string &title, const std::string &mainInfo)
00294     {
00295         std::string contentStr;
00296 
00297         if (style == ARGXStyle::Professional)
00298         {
00299             for (const auto &x : this->options)
00300             {
00301                 // Main option header line
00302                 contentStr += "ID: " + x.id + "\n";
00303                 contentStr += "[ " + x.sparam + " | " + x.param;
00304 
00305                 if (x.hasSubParams && !x.subParams.empty())
00306                 {
00307                     contentStr += " [ ";
00308 
00309                     for (size_t i = 0; i < x.subParams.size(); ++i)
00310                     {
00311                         const auto &sub = x.subParams[i];
00312 
00313                         contentStr += sub.param;
00314 
00315                         if (i < x.subParams.size() - 1) contentStr += " | ";
00316                         else if (i <= x.subParams.size()) contentStr += ' ';
00317                     }
00318 
00319                     contentStr += "] ] ";
00320                 }
00321 
00322                 else contentStr += " ] ";
00323 
00324                 contentStr += x.info + "\n";
00325 
00326                 // Print all sub-options with sparam and param, aligned with ideographic spaces if there are
00327                 if (x.hasSubParams && !x.subParams.empty())
00328                 {
00329                     for (const auto &sub : x.subParams)
00330                     {
00331                         // Create ideographic spaces matching the length of main param for alignment
00332                         std::wstring wideSpaces(x.param.size(), L'\u3000');
00333                         std::wstring_convert<std::codecvt_utf8<wchar_t>> converter;
00334                         std::string spacing = converter.to_bytes(wideSpaces);
00335 
00336                         contentStr += spacing + "  [ " + sub.sparam + " | " + sub.param + " ] " + sub.info + "\n";
00337                     }
00338                 }
00339             }
00340         }
00341 
00342         else if (style == ARGXStyle::Simple)
00343         {
00344             for (const auto &x : this->options)
00345             {
00346                 contentStr += x.sparam + ", " + x.param + " - " + x.info + "\n";
00347 
00348                 if (x.hasSubParams && !x.subParams.empty())
00349                 {
00350                     for (const auto &sub : x.subParams)
00351                     {
00352                         contentStr += "  " + sub.sparam + ", " + sub.param + " - " + sub.info + "\n";
00353                     }
00354                 }
00355             }
00356         } // ARGXStyle
00357 
00358         return title + "\n" + mainInfo + "\n" + contentStr;
00359     }
