00395     int Argx::getWrongArgs(const std::vector<std::string> &argv)
00396     {
00397         int pos = 1; // skip program name
00398         bool isNormalParam = true;
00399 
00400         while (pos < (int)argv.size())
00401         {
00402             const std::string &arg = argv[pos];
00403 
00404             // Find matching top-level option
00405             const ARGXOptions *matched = nullptr;
00406 
00407             for (const auto &opt : this->options)
00408             {
00409                 if (arg == opt.param || arg == opt.sparam)
00410                 {
00411                     matched = &opt;
00412                     break;
00413                 }
00414             }
00415             
00416             // unknown top-level arg
00417             if (!matched) return (isNormalParam ? pos : -pos);
00418 
00419             // matched a top-level option
00420             isNormalParam = true;
00421 
00422             if (matched->hasSubParams || matched->hasAnySubParams)
00423             {
00424                 isNormalParam = false;
00425 
00426                 int scanPos = pos + 1;
00427 
00428                 while (scanPos < (int)argv.size())
00429                 {
00430                     const std::string &nextArg = argv[scanPos];
00431 
00432                     // Is it a declared subparam for this option?
00433                     bool isSub = false;
00434 
00435                     for (const auto &subOpt : matched->subParams)
00436                     {
00437                         if (nextArg == subOpt.param || nextArg == subOpt.sparam)
00438                         {
00439                             isSub = true;
00440                             break;
00441                         }
00442                     }
00443 
00444                     if (isSub)
00445                     {
00446                         // Consume that subparam and continue scanning
00447                         pos = scanPos;
00448                         ++scanPos;
00449 
00450                         continue;
00451                     }
00452 
00453                     // If not a subparam, is it a global option? (do **NOT** consume it)
00454                     bool isGlobalOpt = false;
00455 
00456                     for (const auto &globalOpt : this->options)
00457                     {
00458                         if (nextArg == globalOpt.param || nextArg == globalOpt.sparam)
00459                         {
00460                             isGlobalOpt = true;
00461 
00462                             break;
00463                         }
00464                     }
00465 
00466                     // valid global option follows; stop subparam scan and let outer loop handle it
00467                     if (isGlobalOpt) break;
00468 
00469                     // neither subparam nor global option; it's an invalid sub-parameter token
00470                     return -scanPos;
00471                 }
00472             }
00473 
00474             ++pos;
00475         }
00476 
00477         // If nothing wrong found; return your existing success codes
00478         if (isNormalParam) return (this->mainArgs->size() > 2 ? 2 : 1);
00479         else return (this->mainArgs->size() > 2 ? -2 : -1);
00480     }
