00489     bool Argx::compareArgs(std::vector<ARGXOptions> options, std::vector<std::string> argv)
00490     {
00491         // iterate over argv and skip program name
00492         for (size_t i = 1; i < argv.size(); ++i)
00493         {
00494             const std::string &arg = argv[i];
00495 
00496             // find a matching top-level option
00497             const ARGXOptions *matched = nullptr;
00498 
00499             for (const auto &opt : options)
00500             {
00501                 if (opt.sparam == arg || opt.param == arg)
00502                 {
00503                     matched = &opt;
00504 
00505                     break;
00506                 }
00507             }
00508 
00509             // No matched top-level option
00510             if (!matched) return false;
00511 
00512             // if option supports subparams, try to get them
00513             if (matched->hasSubParams || matched->hasAnySubParams)
00514             {
00515                 size_t j = i + 1;
00516 
00517                 while (j < argv.size())
00518                 {
00519                     const std::string &next = argv[j];
00520 
00521                     // Check the next declared subparam of `matched`
00522                     bool isSub = false;
00523 
00524                     for (const auto &sub : matched->subParams)
00525                     {
00526                         if (next == sub.param || next == sub.sparam)
00527                         {
00528                             isSub = true;
00529 
00530                             break;
00531                         }
00532                     }
00533 
00534                     // Get it and continue scanning for more subparams
00535                     if (isSub)
00536                     {
00537                         ++j;
00538 
00539                         continue;
00540                     }
00541 
00542                     // not a subparam then check if it's a known top-level option
00543                     bool isGlobal = false;
00544 
00545                     for (const auto &g : options)
00546                     {
00547                         if (next == g.param || next == g.sparam)
00548                         {
00549                             isGlobal = true;
00550                             break;
00551                         }
00552                     }
00553 
00554 
00555                     // stop scanning subparams; outer loop will handle this global option
00556                     if (isGlobal) break;
00557 
00558                     // neither a subparam nor a global option to an invalid sequence
00559                     return false;
00560                 }
00561 
00562                 // advance outer index to the last consumed token ( j - 1 ).
00563                 // outer for-loop will increment i, so set `i = j - 1` to continue at j
00564                 if (j > i + 1)
00565                     i = j - 1;
00566             }
00567         }
00568 
00569         return true;
00570     }
