00580     std::vector<std::string> Argx::getSubValue(const std::string &id)
00581     {
00582         // Use `Argx::getArgPos()` function for sub-params
00583         size_t idPos = this->getArgPos(id) + 1;
00584 
00585         if (idPos < 0 || idPos == std::string::npos)
00586             return {this->getOption(id).defaultValue};
00587 
00588         std::vector<std::string> values;
00589 
00590         for (size_t i = idPos ; i < this->getMainArgs().size() ; i++)
00591         {
00592             // End of the sub-parameter finding
00593             // Assume the search is done due to an existsing sub-param
00594             if (i != idPos && this->subParamExists(this->getMainArgs()[i]))
00595                 break;
00596 
00597             values.emplace_back(this->getMainArgs()[i]);
00598         }
00599 
00600         std::string defaultValue;
00601 
00602         {
00603             bool breakOut = false;
00604 
00605             for (size_t i = 0; i < this->mainArgs->size(); ++i)
00606             {
00607                 for (size_t j = 0 ; j < this->options.size() ; ++j)
00608                 {
00609                     if (this->options[i].subParams.size() > j)
00610                     {
00611                         if (this->options[i].subParams[j].id == id)
00612                         {
00613                             defaultValue = this->options[i].subParams[j].defaultValue;
00614 
00615 
00616                             breakOut = true;
00617 
00618                             break;
00619                         }
00620                     }
00621                 }
00622 
00623                 if (breakOut) break;
00624             }
00625         }
00626 
00627         if (values.empty())
00628             values.emplace_back(defaultValue);
00629 
00630         return values;
00631     }
